import React, { useState, useEffect } from 'react';
import {
  Container,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  TextField,
  IconButton,
  Button,
  Typography,
} from '@mui/material';
import { AddCircle, Delete } from '@mui/icons-material';

const ExpenseTracker = () => {
  const [expenses, setExpenses] = useState([]);
  const [projectSuggestions, setProjectSuggestions] = useState([]);

  useEffect(() => {
    // Load saved expenses and project suggestions from local storage
    const savedExpenses = JSON.parse(localStorage.getItem('expenses')) || [];
    const savedSuggestions = JSON.parse(localStorage.getItem('projectSuggestions')) || [];
    setExpenses(savedExpenses);
    setProjectSuggestions(savedSuggestions);
  }, []);

  useEffect(() => {
    // Save expenses and project suggestions to local storage
    localStorage.setItem('expenses', JSON.stringify(expenses));
    localStorage.setItem('projectSuggestions', JSON.stringify(projectSuggestions));
  }, [expenses, projectSuggestions]);

  const handleInputChange = (index, field, value) => {
    const updatedExpenses = [...expenses];
    updatedExpenses[index][field] = value;

    if (field === 'project' && value && !projectSuggestions.includes(value)) {
      setProjectSuggestions([...projectSuggestions, value]);
    }

    if (field === 'quantity' || field === 'unitPrice') {
      const quantity = parseFloat(updatedExpenses[index].quantity) || 0;
      const unitPrice = parseFloat(updatedExpenses[index].unitPrice) || 0;
      updatedExpenses[index].totalPrice = (quantity * unitPrice).toFixed(2);
    }

    setExpenses(updatedExpenses);
  };

  const addNewExpense = () => {
    setExpenses([
      ...expenses,
      { project: '', quantity: 0, unitPrice: 0, totalPrice: '0.00', actualPaid: '' },
    ]);
  };

  const deleteExpense = (index) => {
    const updatedExpenses = expenses.filter((_, i) => i !== index);
    setExpenses(updatedExpenses);
  };

  const calculateTotal = (field) => {
    return expenses
      .reduce((sum, expense) => {
        const value = field === 'actualPaid' && expense[field] === '' ? parseFloat(expense.totalPrice) : parseFloat(expense[field]) || 0;
        return sum + value;
      }, 0)
      .toFixed(2);
  };

  return (
    <Container maxWidth="md" style={{ marginTop: '2rem' }}>
      <Typography variant="h4" gutterBottom align="center">
        Expense Tracker
      </Typography>
      <TableContainer component={Paper} elevation={3}>
        <Table aria-label="expense table">
          <TableHead>
            <TableRow>
              <TableCell><strong>项目</strong></TableCell>
              <TableCell align="right"><strong>数量</strong></TableCell>
              <TableCell align="right"><strong>单价</strong></TableCell>
              <TableCell align="right"><strong>总价</strong></TableCell>
              <TableCell align="right"><strong>实付</strong></TableCell>
              <TableCell align="center"><strong>操作</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {expenses.map((expense, index) => (
              <TableRow key={index} hover>
                <TableCell>
                  <TextField
                    select
                    SelectProps={{
                      native: true,
                    }}
                    value={expense.project}
                    onChange={(e) => handleInputChange(index, 'project', e.target.value)}
                    variant="outlined"
                    size="small"
                    fullWidth
                  >
                    <option value="" disabled>
                      选择项目
                    </option>
                    {projectSuggestions.map((suggestion, i) => (
                      <option key={i} value={suggestion}>
                        {suggestion}
                      </option>
                    ))}
                  </TextField>
                </TableCell>
                <TableCell align="right">
                  <TextField
                    type="number"
                    value={expense.quantity}
                    onChange={(e) => handleInputChange(index, 'quantity', e.target.value)}
                    variant="outlined"
                    size="small"
                    inputProps={{ min: 0 }}
                    fullWidth
                  />
                </TableCell>
                <TableCell align="right">
                  <TextField
                    type="number"
                    value={expense.unitPrice}
                    onChange={(e) => handleInputChange(index, 'unitPrice', e.target.value)}
                    variant="outlined"
                    size="small"
                    inputProps={{ min: 0, step: '0.01' }}
                    fullWidth
                  />
                </TableCell>
                <TableCell align="right">
                  <Typography variant="body1">{expense.totalPrice}</Typography>
                </TableCell>
                <TableCell align="right">
                  <TextField
                    type="number"
                    value={expense.actualPaid}
                    onChange={(e) => handleInputChange(index, 'actualPaid', e.target.value)}
                    variant="outlined"
                    size="small"
                    inputProps={{ min: 0, step: '0.01' }}
                    placeholder={expense.totalPrice}
                    fullWidth
                  />
                </TableCell>
                <TableCell align="center">
                  <IconButton color="error" onClick={() => deleteExpense(index)}>
                    <Delete />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
            <TableRow>
              <TableCell colSpan={4} align="right">
                <Typography variant="h6">总计</Typography>

              </TableCell>
              <TableCell align="right">
                <Typography variant="h6">{calculateTotal('actualPaid')}</Typography>
              </TableCell>
              <TableCell />
            </TableRow>
          </TableBody>
        </Table>
      </TableContainer>
      <Button
        variant="contained"
        color="primary"
        startIcon={<AddCircle />}
        onClick={addNewExpense}
        style={{ marginTop: '1rem' }}
      >
        添加新项目
      </Button>
    </Container>
  );
};

export default ExpenseTracker;
