import React, { useState, useEffect } from 'react';

const ExpenseTracker = () => {
  const [expenses, setExpenses] = useState([]);
  const [projectSuggestions, setProjectSuggestions] = useState([]);

  useEffect(() => {
    const savedExpenses = JSON.parse(localStorage.getItem('expenses')) || [];
    const savedSuggestions = JSON.parse(localStorage.getItem('projectSuggestions')) || [];
    setExpenses(savedExpenses);
    setProjectSuggestions(savedSuggestions);
  }, []);

  useEffect(() => {
    localStorage.setItem('expenses', JSON.stringify(expenses));
    localStorage.setItem('projectSuggestions', JSON.stringify(projectSuggestions));
  }, [expenses, projectSuggestions]);

  const handleInputChange = (index, field, value) => {
    const updatedExpenses = [...expenses];
    updatedExpenses[index][field] = value;

    if (field === 'project' && value && !projectSuggestions.includes(value)) {
      setProjectSuggestions([...projectSuggestions, value]);
    }

    if (field === 'quantity' || field === 'unitPrice') {
      const quantity = parseFloat(updatedExpenses[index].quantity) || 0;
      const unitPrice = parseFloat(updatedExpenses[index].unitPrice) || 0;
      updatedExpenses[index].totalPrice = quantity * unitPrice;
    }

    setExpenses(updatedExpenses);
  };

  const addNewExpense = () => {
    setExpenses([...expenses, { project: '', quantity: '', unitPrice: '', totalPrice: 0, actualPaid: '' }]);
  };

  const calculateTotal = (field) => {
    return expenses.reduce((sum, expense) => {
      const value = field === 'actualPaid' && expense[field] === '' ? expense.totalPrice : expense[field];
      return sum + (parseFloat(value) || 0);
    }, 0).toFixed(2);
  };

  return (
    <div style={containerStyle}>
      <div style={tableContainerStyle}>
        <table style={tableStyle}>
          <thead>
            <tr>
              <th style={headerCellStyle}>项目</th>
              <th style={headerCellStyle}>数量</th>
              <th style={headerCellStyle}>单价</th>
              <th style={headerCellStyle}>总价</th>
              <th style={headerCellStyle}>实付</th>
            </tr>
          </thead>
          <tbody>
            {expenses.map((expense, index) => (
              <tr key={index}>
                <td style={cellStyle}>
                  <input
                    list={`projects-${index}`}
                    value={expense.project}
                    onChange={(e) => handleInputChange(index, 'project', e.target.value)}
                    style={inputStyle}
                  />
                  <datalist id={`projects-${index}`}>
                    {projectSuggestions.map((suggestion, i) => (
                      <option key={i} value={suggestion} />
                    ))}
                  </datalist>
                </td>
                <td style={cellStyle}>
                  <input
                    type="number"
                    value={expense.quantity}
                    onChange={(e) => handleInputChange(index, 'quantity', e.target.value)}
                    style={inputStyle}
                  />
                </td>
                <td style={cellStyle}>
                  <input
                    type="number"
                    step="0.01"
                    value={expense.unitPrice}
                    onChange={(e) => handleInputChange(index, 'unitPrice', e.target.value)}
                    style={inputStyle}
                  />
                </td>
                <td style={cellStyle}>{expense.totalPrice.toFixed(2)}</td>
                <td style={cellStyle}>
                  <input
                    type="number"
                    step="0.01"
                    value={expense.actualPaid}
                    onChange={(e) => handleInputChange(index, 'actualPaid', e.target.value)}
                    placeholder={expense.totalPrice.toFixed(2)}
                    style={inputStyle}
                  />
                </td>
              </tr>
            ))}
            <tr>
              <td colSpan={3} style={totalLabelStyle}>总计</td>
              <td style={totalValueStyle}>{calculateTotal('totalPrice')}</td>
              <td style={totalValueStyle}>{calculateTotal('actualPaid')}</td>
            </tr>
          </tbody>
        </table>
      </div>
      <button onClick={addNewExpense} style={buttonStyle}>
        添加新项目
      </button>
    </div>
  );
};

const containerStyle = {
  padding: '1rem',
  maxWidth: '100%',
  overflowX: 'auto',
};

const tableContainerStyle = {
  overflowX: 'auto',
};

const tableStyle = {
  width: '100%',
  borderCollapse: 'collapse',
  minWidth: '600px', // 确保在小屏幕上有横向滚动
};

const headerCellStyle = {
  backgroundColor: '#f3f4f6',
  padding: '0.75rem',
  borderBottom: '1px solid #e5e7eb',
  textAlign: 'left',
  whiteSpace: 'nowrap',
};

const cellStyle = {
  padding: '0.75rem',
  borderBottom: '1px solid #e5e7eb',
};

const inputStyle = {
  width: '100%',
  padding: '0.5rem',
  border: '1px solid #d1d5db',
  borderRadius: '0.25rem',
  fontSize: '1rem',
};

const totalLabelStyle = {
  ...cellStyle,
  fontWeight: 'bold',
  textAlign: 'right',
};

const totalValueStyle = {
  ...cellStyle,
  fontWeight: 'bold',
};

const buttonStyle = {
  marginTop: '1rem',
  padding: '0.75rem 1rem',
  backgroundColor: '#3b82f6',
  color: 'white',
  border: 'none',
  borderRadius: '0.25rem',
  cursor: 'pointer',
  fontSize: '1rem',
};

export default ExpenseTracker;
